/**
 * Here will be declarations of any files that can be imported
 * to "*.ts" files. You can freely add needed modules as well.
 */

// Images
declare module '*.png' {
  const imagePath: string;
  export default imagePath;
}

declare module '*.jpg' {
  const imagePath: string;
  export default imagePath;
}

declare module '*.jpeg' {
  const imagePath: string;
  export default imagePath;
}

declare module '*.svg' {
  const imagePath: string;
  export default imagePath;
}

